template <typename CONTAINER, typename FUNC>
string map_join(const CONTAINER& container, FUNC func, const string& separator) {
    stringstream ss;
    for (auto it = container.begin(); it != container.end(); ++it) {
        ss << (it == container.begin() ? "" : separator);
        ss << func(*it);
    }
    return ss.str();
}


